mapper.select:
SELECT mr.name, temp.meeting FROM tb_meeting_room mr LEFT JOIN ( SELECT place, CONCAT( "[", GROUP_CONCAT( JSON_OBJECT('start', DATE_FORMAT(start,'%H:%i'), 'end', DATE_FORMAT(end,'%H:%i'), 'status', `status`, 'time', ROUND(timestampdiff(MINUTE,CONCAT(date," ",start),CONCAT(date," ",end))/30) ) ), "]") AS meeting FROM tb_meeting WHERE type = 2 AND `status` != 2 AND date = #{date}
AND JSON_CONTAINS(members, CAST(#{userId} AS CHAR))
GROUP BY place) temp ON mr.name=temp.place WHERE mr.`status`=1 LIMIT #{start}, #{length}

mapper.update:
UPDATE tb_meeting
SET instance_id=#{instanceId}
WHERE uuid = #{uuid}

mapper.delete:
DELETE
FROM tb_meeting
WHERE id = ${id}
  AND creator_id = #{creatorId}

mapper.select:
SELECT m.id, m.title, u.`name` AS creatorName, IF( creator_id = #{userId}, "true", "false" ) AS isCreator, IF(JSON_CONTAINS(m.members,CAST(#{userId} AS CHAR))=1,"true","false") AS mine, m.date, DATE_FORMAT(m.`start`,'%H:%i') AS `start`, DATE_FORMAT(m.`end`,'%H:%i') AS `end`, JSON_LENGTH ( m.members ) AS num, m.`status`, m.`desc`, m.uuid, m.instance_id AS instanceId FROM tb_meeting m JOIN tb_user u ON m.creator_id = u.id WHERE m.type = 1 AND m.`status` != 2
AND m.date = #{date}
AND JSON_CONTAINS(m.members, CAST(#{userId} AS CHAR))
ORDER BY m.id DESC LIMIT #{start}, #{length}

SELECT COUNT(*) FROM tb_meeting WHERE type = 1 AND `status` != 2
AND date = #{date}
AND JSON_CONTAINS(members, CAST(#{userId} AS CHAR))
