mapper.insert:
INSERT INTO tb_meeting_room
SET name=#{name},
max=#{max}

    ,`desc`=#{desc}


    ,status=#{status}

mapper.update:
UPDATE tb_meeting_room
SET name=#{name},
    max=#{max},
    `desc`=#{desc},
    status=#{status}
WHERE id=#{id}

mapper.select:
SELECT IF( SUM( temp.meetings ) > 0, FALSE, TRUE ) AS result FROM ( SELECT COUNT( m.id ) AS meetings FROM tb_meeting_room mr JOIN tb_meeting m ON m.type=2 AND mr.`name`=m.place WHERE mr.id IN
#{one}
GROUP BY mr.id ) temp

mapper.delete:
DELETE FROM tb_meeting_room
WHERE id IN

    #{one}
