mapper.select:
SELECT r.id, u.id, u.`name`, d.dept_name AS deptName, r.content, r.amount, r.anleihen, r.balance, r.type_id AS typeId, r.`status`, DATE_FORMAT(r.create_time,'%Y-%m-%d') AS createTime, IF(r.user_id = #{currentUserId},"true","false") AS mine FROM tb_reim r JOIN tb_user u ON r.user_id = u.id JOIN tb_dept d ON d.id = u.dept_id WHERE 1=1
AND u.dept_id = #{deptId}
AND r.type_id = #{typeId}
AND r.`status` = #{status}
AND u.id = #{userId}
AND u.name LIKE '%${name}%'
AND r.create_time BETWEEN #{startDate} AND #{endDate}
ORDER BY r.id DESC LIMIT #{start}, #{length}

SELECT COUNT(*)
FROM tb_reim r
JOIN tb_user u ON r.user_id = u.id
JOIN tb_dept d ON d.id = u.dept_id
WHERE 1=1

    AND u.dept_id = #{deptId}


    AND r.type_id = #{typeId}


    AND r.`status` = #{status}


    AND u.id = #{userId}


    AND u.name LIKE '%${name}%'


    AND r.create_time BETWEEN #{startDate} AND #{endDate}

mapper.update:
UPDATE tb_reim
SET instance_id = #{instanceId}
WHERE id = #{id}

mapper.select:
SELECT instance_id AS instanceId
FROM tb_reim
WHERE id = #{id}

mapper.delete:
DELETE FROM tb_reim
WHERE id = #{id}
  AND user_id = #{userId}
  AND status IN(1,2)
